INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('arrangement', 'n', 'He can play a piano arrangement of an old folk song.', 1, '2019-10-09 18:33:03', '2019-10-09 18:33:03', '他会弹奏由古老民谣改编的钢琴曲。');
