INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('arrangement', 'n', 'They made an arrangement to meet at the schoolgate at 3 pm.', 1, '2019-10-09 18:33:03', '2019-10-09 18:33:03', '他们约定下午三点钟在校门口见面。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('arrangement', 'n', 'This arrangement is agreeable to both sides.', 1, '2019-10-09 18:33:03', '2019-10-09 18:33:03', '这样安排对双方都合适。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('arrangement', 'n', 'We have made all the arrangements for the conference.', 1, '2019-10-09 18:33:03', '2019-10-09 18:33:03', '我们已做好会议的全部筹备工作。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('arrow', 'n', 'The arrow flew straight and true to its mark.', 1, '2019-10-09 18:33:01', '2019-10-09 18:33:01', '那箭不偏不斜地直朝目标飞去。');
