INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('arrow', 'n', 'The arrow just split the apple in half.', 1, '2019-10-09 18:33:01', '2019-10-09 18:33:01', '箭把苹果分成两半。');
