INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('ash', '(复)ashes：骨灰；废墟；苍白', 'Marilyn''s pitted skin, breasts of carved pumice, volcanic thighs, a face of ash.', 1, '2019-10-09 18:33:00', '2019-10-09 18:33:00', '玛丽莲凹凸不平的皮肤，浮石雕刻般的双乳，火山似的大腿，死一样苍白的脸。');
