INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('ash', 'n', 'She dusted some ash from her sleeve.', 1, '2019-10-09 18:33:00', '2019-10-09 18:33:00', '她撢去袖子上的灰末。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('ash', 'n', 'The fire reduced the house to a mass of ash.', 1, '2019-10-09 18:33:00', '2019-10-09 18:33:00', '大火把房子烧成了灰烬。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('ash', 'n', 'There was an ash tree in the forest which was thought to be a monster.', 1, '2019-10-09 18:33:00', '2019-10-09 18:33:00', '森林中有一株被认为是妖怪的白蜡树。');
