INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('ashamed', 'adj', 'As for you, you should feel ashamed.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '至于你，你应该感到惭愧。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('ashamed', 'adj', 'He is ashamed for his former dishonorable action.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '他对他以前不光彩的行为感到羞愧。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('ashamed', 'adj', 'He jumped up,looking a little ashamed of himself.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '他跳了起来，显得有点惭愧。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('ashamed', 'adj', 'He was ashamed that he had lied.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '他很惭愧他说了谎。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('ashamed', 'adj', 'She seemed ashamed, and turned away as we went by.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '她似乎很害臊，所以在我们走过的时候，她把头扭过去了。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('ashamed', 'adj', 'You ought to feel ashamed of yourself for having descended to such falsehood.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '你竟然堕落到这样说谎的地步，自己应该感到害臊。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('aside', 'adv', 'Before it could be spoken another thought turned it aside.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '还没有来得及讲出口,却让另外一个念头把它岔了开去。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('aside', 'adv', 'I shall have to go aside for a few moments.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '我必须离开一会儿。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('aside', 'adv', 'She has cast her old friends aside.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '她把老朋友都撇在一边。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('aside', 'adv', 'We turned aside from the main road.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '我们离开大道。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('aside', 'n', 'He spoke in an aside of his family.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '他离开本题谈起他的家庭。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('aside', 'n', 'This is surely an author''s aside.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '这当然是作者的旁白。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('assistant', 'adj', 'She is an assistant teacher in our school.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '她是我们学校的助理教员。');
