INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('astonish', 'vt', 'A matter of repeated occurrence like this will not astonish people.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '重复出现的事物不会使人吃惊。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('astonish', 'vt', 'I was astonished at the news of his escape.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '听到他逃之夭夭的消息，我感到惊讶。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('astonish', 'vt', 'She astonished me with her beautiful handwriting.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '她以其秀丽的书法而使我惊异。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('astronomy', 'n', 'Astronomy is the scientific study of the sun, moon, and stars and other heaven bodies.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '天文学是研究太阳、月亮、星星和其他天体的科学。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('astronomy', 'n', 'Astronomy, as distinct from astrology, is an exact science.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '天文学是一门严谨的科学，与占星术完全不同。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('astronomy', 'n', 'Physics and astronomy are cognate sciences.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '物理学和天文学是相关联的科学。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('astronomy', 'n', 'We''re at cross-purposes: I''m talking about astronomy, you''re talking about astrology.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '我们彼此误解了: 我谈的是天文学，你谈的是占星术。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('athlete', 'n', 'She will make a very good athlete.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '她将来会成为一名很好的运动员。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('athlete', 'n', 'The athlete has an electrifying burst of speed.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '这名运动员具有惊人的爆发速度。');
