INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('Atlantic', 'adj', 'A strong gale swept down on the atlantic coast.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '狂风突然猛袭大西洋海岸。');
