INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('atmosphere', 'n', 'The atmosphere changed as soon as she walked in.', 1, '2019-10-09 18:33:14', '2019-10-09 18:33:14', '她一进来气氛就变了。');
