INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('atmosphere', 'n', 'The atmosphere of the city is very much polluted.', 1, '2019-10-09 18:33:14', '2019-10-09 18:33:14', '那个城市的空气受到严重污染。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('atmosphere', 'n', 'The atmosphere over dinner was warm and friendly.', 1, '2019-10-09 18:33:14', '2019-10-09 18:33:14', '用餐时洋溢著热情友好的气氛。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('atmosphere', 'n', 'The atmosphere pressure gradient is an inverse measure of the spacing of the isobars on a weather map.', 1, '2019-10-09 18:33:14', '2019-10-09 18:33:14', '气压梯度与天气图上等压线的间隔大小成反比。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('atmosphere', 'n', 'The burning of fossil fuels results in the pollution of the atmosphere.', 1, '2019-10-09 18:33:14', '2019-10-09 18:33:14', '矿物燃料的燃烧导致了大气污染。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('atmosphere', 'n', 'The water droplets ejected by the gun expand in the atmosphere because it is at lower pressure than the air used to make the droplets.', 1, '2019-10-09 18:33:14', '2019-10-09 18:33:14', '由于大气气压比造雪炮中气压低，雪炮中的水滴被喷射到空气中。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('atom', 'n', 'A molecule of water consists of two atoms of hydrogen and one atom of oxygen.', 1, '2019-10-09 18:33:04', '2019-10-09 18:33:04', '水分子含有两个氢原子和一个氧原子。');
