INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('atom', 'n', 'That cannot get up an atom of sympathy for them.', 1, '2019-10-09 18:33:04', '2019-10-09 18:33:04', '那并不能激发起人们对他们的丝毫同情。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('atom', 'n', 'The atom bomb is the offspring of the 20th century physics.', 1, '2019-10-09 18:33:04', '2019-10-09 18:33:04', '原子弹是20世纪物理学的产物。');
