INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('attack', 'n', 'His attack on the government was pure vitriol.', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '他纯粹出于恶意抨击政府。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('attack', 'n', 'The police are launching a major attack on drug dealers.', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '警方对毒品贩子发动了大规模的攻击。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('attack', 'v', 'It is reported that this disease attacks the central nervous system.', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '据报道说这种疾病破坏中枢神经系统。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('attack', 'v', 'Once you show any sign of fear, he will attack you.', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '你一旦露出任何恐惧的迹象,他就会攻击你。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('attack', 'v', 'The enemy attacked us at night.', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '敌人在夜里向我们进攻。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('attack', 'v', 'There is a newspaper article attacking the Prime Minister.', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '报纸上有一篇抨击首相的文章.');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('attempt', 'n', 'He studied hard in an attempt to pass the exam.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '他努力学习试图通过考试。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('attempt', 'n', 'The enemy failed in their attempt to land on the island.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '敌人在岛上登陆的企图失败了。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('attempt', 'v', 'He attempted to smother up the scandal about his family.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '他企图掩饰有关他家庭的丑闻。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('attempt', 'v', 'I would be the last to attempt to answer the question.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '我是最不可能去尝试回答这个问题。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('attentively', 'adv', 'Ask her what happened and listen sympathetically and attentively.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '问受害者所发生的事，并同情地及注意地听。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('attentively', 'adv', 'If there is a chat among us, she is always the one who says little but listens attentively.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '如果有在我们之中的闲谈，她总是少说但是注意地听的人。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('attitude', 'n', 'As you get older, your attitude towards death changes.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '人随著年龄的增长,对死亡的看法也会有所改变。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('attitude', 'n', 'He has a mellow attitude to life.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '他对生活有种成熟的看法。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('attitude', 'n', 'She shows a very positive attitude to her work.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '她工作态度非常积极。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('attitude', 'n', 'They sat around in easy attitude.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '他们以舒适的姿势围坐在一起。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('attitude', 'n', 'We must maintain a firm attitude.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '我们必须采取坚定的态度。');
