INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('breathe', 'vt', 'Promise me you won''t breathe a wordEn of this to anyone.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '答应我别将此事泄漏给任何人。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('brick', 'n', 'He bought a brick of cheese and a bottle of wine.', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '他买了一块乳酪和一瓶葡萄酒。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('brick', 'n', 'He had a brick thrown at him at that meeting.', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '那次集会时，他的头部被一块砖头击中。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('brick', 'n', 'Her husband is a brick layer.', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '她丈夫是个砌砖匠。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('brick', 'n', 'His new house was built by himself, brick by brick.', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '他的新房子是他自己一块砖一块砖砌起来的。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('brick', 'vt', 'All the windows were bricked up.', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '所有的窗户都用砖堵住了。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('bride', 'n', 'I suppose we shall be having some sort of celebration for the bride.', 1, '2019-10-09 18:33:06', '2019-10-09 18:33:06', '我想我们还是来为新娘庆贺一番吧。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('bride', 'n', 'The bride brought many dowries to her husband.', 1, '2019-10-09 18:33:06', '2019-10-09 18:33:06', '新娘带着很多嫁妆给她丈夫。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('bride', 'n', 'The bride was given away by her father.', 1, '2019-10-09 18:33:06', '2019-10-09 18:33:06', '由父亲把新娘交与新郎。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('bride', 'n', 'The bride wore a beautiful white dress.', 1, '2019-10-09 18:33:06', '2019-10-09 18:33:06', '新娘穿了一身美丽的白色礼服。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('bride', 'n', 'We must drink to the health of the bride.', 1, '2019-10-09 18:33:06', '2019-10-09 18:33:06', '我们要为新娘的健康干杯。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('bridegroom', 'n', 'Bride and bridegroom start from Mohandseen.', 1, '2019-10-09 18:33:03', '2019-10-09 18:33:03', '新郎和新娘一起从婚纱影楼出发。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('bridegroom', 'n', 'The bridegroom was dressed in a grey suit.', 1, '2019-10-09 18:33:03', '2019-10-09 18:33:03', '新郎穿一套灰色礼服。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('brief', 'adj', 'I don''t want to keep you long, so let me be brief.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '我不想浪费大家的时间,就简明扼要地讲几句吧。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('brief', 'adj', 'In the beginning, the teacher gave us a brief introduction about the subject.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '开始时,老师给我们做了关于这门课程的简短介绍。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('brief', 'adj', 'There was a brief pause in the conversation.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '对话中有短暂的停顿。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('brief', 'adj', 'We have a tight schedule for your brief visit. I hope you don''t mind.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '对您短暂的访问,我们为您安排的日程很紧,希望您不介意。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('brief', 'n', 'He drew up a brief for his speech.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '他起草了一份讲话的摘要。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('brief', 'n', 'The lawyers declared on a court brief that they will argue Mr. Brown''s innocence by reason of insanity.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '律师们在呈交法庭的辩护状上声明,他们将以神经错乱为由为布朗先生作无罪辩护。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('brief', 'vt', 'I''d like to ask Mr. Dean to brief us on the situation.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '现在由院长先生来简单介绍一下形势。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('brief', 'vt', 'The chairman will brief the Board on the most recent developments.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '主席将向董事会概述最近的发展。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('broadcast', 'n', 'Here is a political party broadcast before an election.', 1, '2019-10-09 18:33:03', '2019-10-09 18:33:03', '这是某党派选举前的政治广播节目。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('broadcast', 'n', 'The broadcast was recorded, not live.', 1, '2019-10-09 18:33:03', '2019-10-09 18:33:03', '这次广播是事先录音的，不是现场直播。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('broadcast', 'v', 'Before this play is broadcast several cuts must be made.', 1, '2019-10-09 18:33:03', '2019-10-09 18:33:03', '这个剧目播出前必须经过数次剪辑。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('broadcast', 'v', 'Broadcast of seeds is done by hand.', 1, '2019-10-09 18:33:03', '2019-10-09 18:33:03', '用手撒播种子。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('broadcast', 'v', 'She broadcasts on current affairs.', 1, '2019-10-09 18:33:03', '2019-10-09 18:33:03', '她发表时事广播演说。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('broadcast', 'v', 'The BBC broadcasts all over the world.', 1, '2019-10-09 18:33:03', '2019-10-09 18:33:03', '英国广播公司向全世界播送节目。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('broadcast', 'v', 'The chairman''s speech will be broadcast nationwide.', 1, '2019-10-09 18:33:03', '2019-10-09 18:33:03', '主席的讲话将向全国广播。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('broadcast', 'v', 'You don''t have to broadcast my news to the whole world!', 1, '2019-10-09 18:33:03', '2019-10-09 18:33:03', '你不能把我的事告知全天下。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('brotherhood', 'n', 'He believes in the brotherhood of all peoples.', 1, '2019-10-09 18:33:05', '2019-10-09 18:33:05', '他相信各民族间都应亲如兄弟。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('brotherhood', 'n', 'I value the brotherhood between us.', 1, '2019-10-09 18:33:05', '2019-10-09 18:33:05', '我珍视我们之间兄弟般的情谊。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('brotherhood', 'n', 'Soldiers who are fighting together often have a strong feeling of brotherhood.', 1, '2019-10-09 18:33:05', '2019-10-09 18:33:05', '一起作战的士兵相互间常怀有深厚的兄弟情谊。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('brunch', 'n', 'Brunch is a cross between breakfast and lunch.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '早午餐是介于早餐兴午餐的中间餐。');
