INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('brush', 'n', 'He had a brush with his neighbour.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '他和邻居发生了一点摩擦。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('brush', 'n', 'He painted the wall with a brush.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '他用刷子涂墙。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('brush', 'n', 'I''ll give my coat a good brush.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '我要把我的衣服好好刷一下。');
