INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('brush', 'n', 'She has a nasty brush with her boss this morning.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '今天早晨她跟老板大吵了一顿。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('brush', 'n', 'The painter swept a brush over his canvas.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '画家的画笔在画布上飞掠。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('brush', 'vi', 'A cat brushed against her leg in the darkness.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '黑暗中,一只猫蹭着了她的腿。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('brush', 'vi', 'Don''t bother to move.I think I can just brush past.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '不必麻烦动了,我想我能擦身挤过去。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('brush', 'vt', 'He brushed the dust off his clothes.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '他掸掉衣服上的灰尘。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('brush', 'vt', 'I''ll have to give my shoes a brush.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '我必须把我的鞋擦一下。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('brush', 'vt', 'She is scouring the floor with a brush.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '她正在用刷子擦地板。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('brush', 'vt', 'The light wind gently brushed her cheek.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '微风轻拂着她的脸颊。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('brush', 'vt', 'We should brush our teeth at least twice a day.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '我们每天应该至少刷两次牙。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('bucket', 'n', 'Go and fill this bucket with water for me.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '去替我装满一桶水来。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('bucket', 'n', 'He came down the hill carrying a bucket of water.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '他提著一桶水下山来了。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('bucket', 'n', 'I knocked over the bucket and the water poured (out) all over the floor.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '我打翻了水桶，水流了一地。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('bucket', 'n', 'if it rains in buckets, it rains very hard.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '如果下的倾盆大雨,说明雨下得很大。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('bucket', 'n', 'They buy tickets by the bucket and sell them at a profit.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '他们买了很多票,并且再卖掉以获取利润。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('bucket', 'vi', 'The old car bucketed along at about thirty.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '那辆旧车以30公里左右的时速行驶。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('bucket', 'vi', 'The rain bucketed down.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '大雨倾盆。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('bucket', 'vt', 'The girl was bucketing water from the well.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '那姑娘在用桶从井里打水。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('Buddhism', 'n', 'Mr White has converted to Buddhism.', 1, '2019-10-09 18:33:04', '2019-10-09 18:33:04', '怀特先生已改信佛教。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('Buddhism', 'n', 'Some young people study Zen Buddhism or Bahai.', 1, '2019-10-09 18:33:04', '2019-10-09 18:33:04', '有些年轻人研究禅宗佛教或伊斯兰巴哈教派的教义。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('Buddhist', 'adj', 'Buddhist practice and faith involves finding inner peace and calm.', 1, '2019-10-09 18:32:59', '2019-10-09 18:32:59', '佛教的修行和信仰包括追求的是内心的平静。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('Buddhist', 'adj', 'He''s fascinated with Buddhist ceremonies.', 1, '2019-10-09 18:32:59', '2019-10-09 18:32:59', '他迷上了佛教的仪式。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('Buddhist', 'n', 'How do we become a devout Buddhist?', 1, '2019-10-09 18:32:59', '2019-10-09 18:32:59', '要如何成为虔诚的佛教徒？');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('Buddhist', 'n', 'The old Buddhist wouldn''t harm a fly.', 1, '2019-10-09 18:32:59', '2019-10-09 18:32:59', '那个老佛教徒不愿意伤害一只苍蝇。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('burial', 'n', 'He was given an honorable burial.', 1, '2019-10-09 18:33:04', '2019-10-09 18:33:04', '他的葬礼很体面。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('burial', 'n', 'Heaven gives life and earth provides the burial place.', 1, '2019-10-09 18:33:04', '2019-10-09 18:33:04', '天赐生命，地供坟墓。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('burial', 'n', 'His body was brought back to England for burial.', 1, '2019-10-09 18:33:04', '2019-10-09 18:33:04', '他的遗体已运回英国埋葬。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('burial', 'n', 'In the opening verse we find the women coming to the tomb to anoint the body for burial.', 1, '2019-10-09 18:33:04', '2019-10-09 18:33:04', '在本章一开始的经节中，我们看到一些妇女来到坟墓前，为埋葬耶稣用油膏耶稣的身体。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('burial', 'n', 'The body was placed in bier before burial.', 1, '2019-10-09 18:33:04', '2019-10-09 18:33:04', '在举行葬礼以前尸体被放在棺材里。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('burial', 'n', 'The burial will be on Wednesday.', 1, '2019-10-09 18:33:04', '2019-10-09 18:33:04', '遗体将在周三被埋葬。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('bury', 'vt', 'Even if you kill me, even if you bury me, I should rise again.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '即使你杀了我');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('bury', 'vt', 'He likes to bury himself in the country.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '他喜欢隐居乡间。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('bury', 'vt', 'He was sitting with his head buried in a book.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '他坐着埋头看书。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('bury', 'vt', 'Just bury the hatchet and go have a drink together!', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '别吵了，一起去喝一杯吧！');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('bury', 'vt', 'She had learnt to bury her feelings.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '她已经学会了感情不外露。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('bury', 'vt', 'She willed herself to bury her past.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '她努力使自己忘记过去。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('bury', 'vt', 'The funeral is tomorrow and they will bury the body after the church service.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '葬礼明天举行，他们会在教堂仪式后埋葬遗体。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('bury', 'vt', 'You will have to bury yourself in your studies if you want to pass the exam.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '如果你想通过考试，就得专心致志地学习。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('businessman', 'n', 'That businessman flies great distances every month.', 1, '2019-10-09 18:32:59', '2019-10-09 18:32:59', '那个商人每月都乘飞机来回很远的地方。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('businessman', 'n', 'The businessman is a proverb for meanness.', 1, '2019-10-09 18:32:59', '2019-10-09 18:32:59', '那个商人因行为卑鄙而恶名远扬。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('businesswoman', 'n', 'A businesswoman created a special shelter to house injured animals.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '一个女商人找到了一所房子饲养这些狗。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('businesswoman', 'n', 'A Chinese businesswoman became very rich recycling waste paper.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '一名中国的女商人靠循环利用废纸发了财。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('butcher', 'n', 'Hitler was as a great a butcher as the world has seen.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '希特勒是有史以来最大的侩子手。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('butcher', 'n', 'The butcher chopped off a rib of frozen pork.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '屠夫砍下一块冻肉的肋条。');
