INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('faith', 'n', 'Missionaries went far afield to propagate their faith.', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '传教士到远方去传播其信仰。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('faith', 'n', 'The disaster caused him to waver in his faith.', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '这件灾难使他对信仰发生了动摇。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fall', 'n', 'Food prices lowered during the fall.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '秋天食品价格下降了。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fall', 'n', 'I had a fall (from a horse) and broke my arm.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '我（从马上）跌下来把胳膊摔断了。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fall', 'n', 'The falls upstream are full of salmon.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '该瀑布上流有许多大麻哈鱼。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fall', 'v', 'Apples fall from the tree.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '苹果从树上落下。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fall', 'v', 'Jobs used to disappear when the snow fell.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '当雪天来临时，就业机会就会消失。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fall', 'v', 'The knife fell from his nerveless hand.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '那把刀从他无力的手中落下。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fall', 'v', 'The tree fell with a thunderous crash.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '那棵树轰隆一声倒下了。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('false', 'adj', 'After several false starts, she became a successful journalist.', 1, '2019-10-09 18:33:16', '2019-10-09 18:33:16', '她开始时遇到几次挫折,后来成为能干的记者');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('false', 'adj', 'Lucy had a pair of false eyelashes today.', 1, '2019-10-09 18:33:16', '2019-10-09 18:33:16', '露西今天戴了一副假睫毛。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('false', 'adj', 'The impostor traveled on a false passport.', 1, '2019-10-09 18:33:16', '2019-10-09 18:33:16', '那个伪装者用一本假护照旅行。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('false', 'adj', 'There is no time for false modesty.', 1, '2019-10-09 18:33:16', '2019-10-09 18:33:16', '没时间虚情假意地故作谦虚了。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('false', 'adj', 'They lulled me into a false sense of security.', 1, '2019-10-09 18:33:16', '2019-10-09 18:33:16', '他们哄骗我，让我产生了一种虚假的安全感。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('false', 'adv', 'He is just playing me false, which never surprises me.', 1, '2019-10-09 18:33:16', '2019-10-09 18:33:16', '他只是在欺骗我,那一点我从来不感到奇怪。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fancy', 'adj', 'A fancy hairstyle will be great for a party.', 1, '2019-10-09 18:33:04', '2019-10-09 18:33:04', '参加晚会梳个时尚发型很不错。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fancy', 'adj', 'All eyes were rested on the fancy car.', 1, '2019-10-09 18:33:04', '2019-10-09 18:33:04', '所有的眼神都注视那部新奇的车。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fancy', 'adj', 'He sells poor goods and charges fancy prices.', 1, '2019-10-09 18:33:04', '2019-10-09 18:33:04', '他出售的商品很糟,要价却非常昂贵。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fancy', 'adj', 'She wants to buy a handbag, not fancy but strong.', 1, '2019-10-09 18:33:04', '2019-10-09 18:33:04', '我想买一个手提包不要花俏,而要耐用。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fancy', 'adj', 'The store sells fancy fresh fruits.', 1, '2019-10-09 18:33:04', '2019-10-09 18:33:04', '这商店出售优质的鲜果。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fancy', 'adj', 'These fancy arguments befuddled us.', 1, '2019-10-09 18:33:04', '2019-10-09 18:33:04', '这些复杂的论据令我们疑惑不已。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fancy', 'n', 'I have taken a fancy to that new bicycle.', 1, '2019-10-09 18:33:04', '2019-10-09 18:33:04', '我非常喜欢那辆新自行车。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fancy', 'n', 'In the spring, a young man''s fancy lightly turns to thoughts of love.', 1, '2019-10-09 18:33:04', '2019-10-09 18:33:04', '春天,年轻人的想像力很容易转移到关于爱情的遐想上去了。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fancy', 'n', 'The painting took his fancy, so he bought it.', 1, '2019-10-09 18:33:04', '2019-10-09 18:33:04', '这幅画被他看中了,所以他就把它买了下来。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fancy', 'n', 'You have to discriminate between fact and fancy.', 1, '2019-10-09 18:33:04', '2019-10-09 18:33:04', '你得分清事实与幻想。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fancy', 'vt', 'He is not at all the kind of man I fancy.', 1, '2019-10-09 18:33:04', '2019-10-09 18:33:04', '他根本不是我所喜欢的人。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fancy', 'vt', 'I fancy that my friend will be all right.', 1, '2019-10-09 18:33:04', '2019-10-09 18:33:04', '我希望我的朋友平安无事。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fancy', 'vt', 'I fancy they''re getting on well with each other.', 1, '2019-10-09 18:33:04', '2019-10-09 18:33:04', '我猜想他们相处得不错。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fantasy', 'n', 'He is unable to divorce fantasy from reality.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '他不能将幻想与现实分开。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fantasy', 'n', 'The young always live in a world of fantasy.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '年轻人总是生活在幻想中。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fantasy', 'v', 'He fantasies himself a doctor.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '他想像自己是一位医生。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fasten', 'vi', 'It is advisable that you fasten on the opportunity.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '你抓住这个机会是明智的。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fasten', 'vi', 'The handbag won''t fasten properly.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '这手提包的口关不紧。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fasten', 'vi', 'What can I use to fasten on this clip?', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '我用什么才能将这个夹子固定好?');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fasten', 'vt', 'Do not fasten all the windows up while you sleep.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '睡觉时不要将所有的窗户都关紧。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fasten', 'vt', 'Don''t fasten the responsibility on him.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '不要把责任推到他身上。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fasten', 'vt', 'For your own safety,please fasten your seat belts.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '安全起见，请系紧安全带。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fasten', 'vt', 'I fastened the pages together with a paperclip.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '我用一个纸夹把那几页纸夹在一起。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fasten', 'vt', 'The little boy has learnt to fasten shoelaces.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '这小男孩已学会系鞋带了。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fault', 'n', 'He has many faults, but vanity is not one of them.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '他有很多缺点，但并没有虚荣心。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fault', 'n', 'I like him despite his faults.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '虽然他有很多缺点，我仍然喜欢他。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fault', 'n', 'It was not our fault that we were late.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '这次迟到不是我们的错。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fault', 'n', 'It''s partly your fault.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '一方面也是由于你的过错。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fault', 'n', 'The Balcones Fault is the geological dividing line.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '鲍尔肯断层是地理分界线。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fault', 'n', 'There is a fault in the electrical system.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '电路系统出了故障。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fault', 'n', 'They built it right over a geological fault.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '它正好建在一个地质断层上。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fault', 'v', 'No one could fault his performance.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '他的演出无懈可击.');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fax', 'n', 'After you think it over, please let me know by fax.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '你仔细考虑过之后，请用传真相告。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fax', 'n', 'I was on vacation last week and didn''t receive your fax.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '上星期我正在度假，没收到你们的传真。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fax', 'n', 'Reservations can be made by letter or fax.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '可以来信或传真预订。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fax', 'n', 'We receive a fax of the order this morning.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '我们今天上午收到一份传真订单。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fax', 'v', 'Could you fax me the latest version?', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '你可不可以把最新版本传真给我?');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fax', 'v', 'My secretary will fax all my travel plans.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '我的秘书会传真给你我的所有行程。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fee', 'n', 'I''m afraid I can''t afford the doctor''s fee.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '恐怕我付不起给医生的酬金。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fee', 'n', 'She charged only a nominal fee for her work.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '她为所做的工作只收取象征性的费用。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fee', 'n', 'The doctor''s fee was higher than we expected.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '这位医生的收费比我们预料的高。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fee', 'n', 'The fee they are offering bears no relation to the amount of work involved.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '他们支付的酬金和所需的工作量无关。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fee', 'n', 'Tickets are issued only against payment of the full fee.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '只有付讫全费才可将票发出。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fell', 'adj', 'He was struck by a fell blow to the head.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '他的头部受到了重击。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fell', 'n', 'The wind and rain beat down on me, and I fell down several times, but finally I arrived at a long, low house, standing rather isolated in the middle of the moor.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '风裹着雨打在我的身上，我跌倒了好几次，但终于还是走到了一幢孤零零立在沼泽地中间的长长的矮房子前。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fell', 'n', 'This is the famous Lakeland Fells.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '这就是著名的莱克兰荒野。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fell', 'vt', 'He felled his enemy with a single blow.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '他一拳将他的敌人打倒了。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fell', 'vt', 'The lumberman will fell these great trees.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '这位伐木工将砍伐掉这些大树。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fell', '动词fall的过去式', 'I managed to grab hold of the jug before it fell.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '我设法抓住了那个罐子才未跌落。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fell', '动词fall的过去式', 'No sooner had he arrived than he fell sick.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '他刚抵达就病倒了。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fell', '动词fall的过去式', 'The girl lost her balance and fell off the balance beam.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '小女孩失去了平衡，从平衡木上摔了下来。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fell', '动词fall的过去式', 'The knife fell from her nerveless fingers.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '刀子从她那无力的手中落下。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fellow', 'adj', 'He had consulted with his fellow workers before he made the decision.', 1, '2019-10-09 18:33:14', '2019-10-09 18:33:14', '在做决定之前,他跟同事们商量过。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fellow', 'adj', 'He is popular with his fellow workers.', 1, '2019-10-09 18:33:14', '2019-10-09 18:33:14', '他在同事之间很受欢迎。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fellow', 'adj', 'He recalled his fellow traveller with pleasure.', 1, '2019-10-09 18:33:14', '2019-10-09 18:33:14', '他愉快地回忆起他的旅伴。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fellow', 'n', 'He was cut off from his fellows.', 1, '2019-10-09 18:33:14', '2019-10-09 18:33:14', '他和同伴失去联系。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fellow', 'n', 'He was elected a fellow of Trinity College in Cambridge and assistant tutor.', 1, '2019-10-09 18:33:14', '2019-10-09 18:33:14', '他当选为剑桥的三一学院的研究员和助理导师。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fellow', 'n', 'He''s a very nice fellow though bad-tempered.', 1, '2019-10-09 18:33:14', '2019-10-09 18:33:14', '他虽然脾气暴躁,但是一个很好的同事。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fellow', 'n', 'I can''t tolerate that rude fellow.', 1, '2019-10-09 18:33:14', '2019-10-09 18:33:14', '我不能容忍那个粗鲁的家伙。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fellow', 'n', 'Jack is a clever fellow indeed.', 1, '2019-10-09 18:33:14', '2019-10-09 18:33:14', '杰克确实是个聪明的家伙。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fellow', 'n', 'Joe is a fellow of the Law College.', 1, '2019-10-09 18:33:14', '2019-10-09 18:33:14', '乔是法学院的研究员。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fellow', 'n', 'That foolish fellow is looking for trouble.', 1, '2019-10-09 18:33:14', '2019-10-09 18:33:14', '那个愚蠢的家伙在自找麻烦。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fence', 'n', 'He hired a workman to repair the fence.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '他雇用了一个工人修理围墙。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fence', 'n', 'He tried to see over the fence but it was too high.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '他试图从栅栏上面看过去，但它太高了。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fence', 'n', 'That fence is the limit of the schoolyard.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '那堵围墙是校园的界限。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fence', 'n', 'The loot had disappeared and been handled by a fence.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '赃物失踪后被一个销脏的人处理掉了。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fence', 'v', 'I should like the chance to fence with a worthier opponent.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '我希望有机会跟实力相当的对手击剑。');
