INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fond', 'adj', 'She has many shortcomings, but we''re all very fond of her.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '虽然她有许多缺点，但我们都很喜欢她。');
