INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('fool', 'v', 'He is trying to fool you,don''t listen to him.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '他想愚弄你，别听他的。');
