INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('minimum', 'n', 'Consolidate everything to the bare minimum.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '把一切巩固到最低限度的最小量。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('minimum', 'n', 'Repairing your car will cost a minimum of 100 dollars.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '修理你的汽车最低限度要100美元。');
