INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('miniskirt', 'n', 'It''s an era of the miniskirt in the seventies.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '七十年代是超短裙时代。');
