INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('minister', 'n', 'He was elected Minister of Education.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '他被选为教育部长。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('minister', 'n', 'The Chancellor of the Exchequer is the minister in charge of finance in Britain.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '英国财政大臣是负责财政的大臣。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('minister', 'n', 'The minister can only act in this matter with the sanction of Parliament.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '部长只有得到国会批准才能就此事采取行动。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('minister', 'n', 'The minister preached a sermon on humility.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '牧师就谦恭的问题布道。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('minister', 'n', 'The president gave his minister carte blanche in foreign affairs.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '总统把处理外交事务的全权授予他的公使。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('minister', 'v', 'I have to minister to my mother all the time.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '我不得不一直照料我的母亲。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('minister', 'v', 'She felt it was her vocation to minister to the sick.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '她觉得照料病人是她的天职。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('mist', 'n', 'A mist of perfume hung in the air.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '空气中飘溢著香水的雾气。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('mist', 'n', 'Everything was covered in mist.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '一切都笼罩在雾霭中。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('mist', 'n', 'I turn from my work and my eyes fill with the mist.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '我劳作回来眼神朦胧不清。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('mist', 'n', 'She could hardly recognize her son through the mist of tears that filled her eyes.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '她泪眼蒙蒙，几乎不能认出自己的儿子。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('mist', 'vi', 'But when he wakes, hours later, the dream has turned to mist.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '但是当他醒来之后,过了几个钟头,这些梦就变成了一团迷雾。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('mist', 'vt', 'Humidity would mist his glasses and slick his bald head with sweat.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '湿气模糊了眼镜，汗水却令头顶更加光亮。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('mist', 'vt', 'Our breath is misting up the car windows.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '我们呼出的气渐渐给汽车的窗户上蒙上一层水蒸气。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('mist', 'vt', 'When I drink tea, my glasses mist over.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '我喝茶时眼镜蒙上了一层水汽。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('misunderstand', 'v', 'He wrote her a letter for fear she misunderstand him.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '他给她写了封信，免得她误会。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('misunderstand', 'v', 'His remarks show that he misunderstood my position on the question.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '他的话说明他误解了我在这一问题上的立场。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('misunderstand', 'v', 'I think the couple misunderstand each other.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '我认为这对夫妇未能真正互相了解。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('misunderstand', 'v', 'They pretend to misunderstand my complaint.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '他们假装误解了我的投诉。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('mixture', 'n', 'Air is a mixture, not a compound of gases.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '空气是气体的混合物，不是化合物。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('mixture', 'n', 'She trickles oil into the mixture.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '她把油一点点地注入混合物中。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('mixture', 'n', 'The bottom layer is a mixture of sand and clay.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '最下面的一层是沙和泥的混合物。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('mixture', 'n', 'You need a spoon to beat the mixture smooth.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '你需要用一把调羹来搅均混合液。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('moment', 'n', 'He thought for a moment and then spoke.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '他想了片刻，然後说。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('moment', 'n', 'I''ll be back in a moment.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '我一会儿就回来。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('moment', 'n', 'Love is a moment that lasts forever.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '爱的瞬间就是永恒。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('moment', 'n', 'She answered without a moment''s hesitation.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '她毫不迟疑立即回答。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('moment', 'n', 'The big moment has come at last!', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '重要的时刻终于到来了！');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('moment', 'n', 'The clock is timed to the moment.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '这钟走的准确。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('moment', 'n', 'The first moment is clockwise and the second is counterclockwise.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '第一个力矩为顺时针方向，第二个力矩为反时针方向。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('moment', 'n', 'The largest earthquake ever recorded on the moment magnitude scale measured 9.5.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '曾经被记录的用力矩震级描述的最大的地震是9.');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('moment', 'n', 'The user has to judge the exact moment to stop.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '用户不得不推断出停止的准确时间。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('moment', 'n', 'This is a matter of great moment.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '这是一件非常重要的事。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('moment', 'n', 'Would you take a seat for a moment?', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '请你稍坐片刻好吗?');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('monument', 'n', 'A monument was built to commemorate the victory.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '为了纪念胜利，建造了一座纪念碑。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('monument', 'n', 'It''s the most enigmatic prehistoric monument on earth.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '它是地球上最神秘的史前遗迹。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('monument', 'n', 'Nelson''s Column is a famous monument in London.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '纳尔逊纪念碑是伦敦著名的纪念碑。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('monument', 'n', 'The monument is in honor of a scientist.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '那座纪念碑是为纪念一位科学家而建的。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('monument', 'n', 'The monument was fronted with stone.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '纪念碑的正面是石料做的。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('monument', 'n', 'These recordings are a monument to his talent as a pianist.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '这些录音是展现他钢琴家才华的不朽之作。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('monument', 'n', 'They dedicated a monument in memory of those who died in the great earthquake.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '他们为悼念死于大地震的人们建造纪念碑。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('monument', 'n', 'Time and weather had long ago effaced the inscription on the monument.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '岁月和风雨早已磨掉纪念碑上的铭文。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('mop', 'n', 'Here is a dish mop.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '这儿是洗碗刷。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('mop', 'n', 'I used a mop to sop up the spilled water.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '我用拖把把泼出的水擦干。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('mop', 'n', 'She had a mop of black hair.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '她一头蓬乱的黑发。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('mop', 'vi', 'First she dusted, then she mopped.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '她首先擦掉灰尘, 然后用拖把擦。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('mop', 'vt', 'I have to mop the kitchen floor at least once a day.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '我每天至少要把厨房地板擦洗一次。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('mop', 'vt', 'She mops her tears with a towel.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '她用毛巾擦眼泪。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('moral', 'adj', 'His behavior transgressed the moral rules of the social conduct.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '他的行为违背了社会行为的道德准则。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('moral', 'adj', 'It comprises two main sets of rights: the economic rights and the moral rights.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '版权包括两种主要的权利:经济权利和精神权利。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('moral', 'adj', 'She regards his moral faults with charity.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '她宽恕他的品行不端。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('moral', 'adj', 'We differ about moral standards.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '我们对道德标准看法不一致。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('moral', 'n', 'As a Puritan, he is strict in moral and religion.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '作为清教徒，他的道德和宗教观念都很严格。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('moral', 'n', 'People can draw a moral from tales.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '人们可以从寓言里吸取经验教训。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('moral', 'n', 'She threw aside all her moral teachings and inhibitions.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '她把她所讲的伦理道德全抛到了一边。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('moral', 'n', 'Taking drygs is also a moral and ethical problem.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '用药问题也涉及到伦理道德问题。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('moral', 'n', 'The moral of this story is ‘Better late than never''.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '这个故事的寓意是｀迟做总比不做好＇.');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('moral', 'n', 'You may draw your own moral from it.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '你可以从中得出自己的教训。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('motherland', 'n', 'He expressed earnest love to his motherland.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '他抒发了对祖国的挚爱之情。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('motherland', 'n', 'We are loyal to our motherland.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '我们忠于祖国。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('motorbike', 'n', 'That motorbike will be the death of you.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '那辆摩托车就会要了你的命。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('motorcycle', 'n', 'His parents vetoed his plan to buy a motorcycle.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '他的父母反对他买摩托车的计划。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('motorcycle', 'n', 'My brother''s rinky-dink motorcycle just fell apart.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '我兄弟那辆破摩托车已经解体了。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('motorcycle', 'v', 'The man on the motorcycle wore a helmet.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '骑摩托车的人戴了一顶头盔。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('motorcycle', 'v', 'Who would want to ride a motorcycle here?', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '谁会愿意在这种鬼地方骑摩托车？');
