INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('motto', 'n', 'Humility is the motto in his life.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '谦卑是他的人生座右铭.');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('motto', 'n', 'Who is the subscriber of that motto?', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '谁是那条座右铭的签字者?');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('mountainous', 'adj', 'He lives in a mountainous district.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '他住在山区。');
