INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('mountainous', 'adj', 'The mountainous waves swallowed up the ship.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '山一般的巨浪吞没了那艘航船。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('mourn', 'v', 'People wore crapes to mourn our leader.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '人们戴着黑纱哀悼我们的领导。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('mourn', 'v', 'The old woman still mourns for her son.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '这位老太太还在为儿子的死伤心。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('mourn', 'v', 'We mourn for our fallen soldiers.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '我们哀悼阵亡的兵士。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('mourn', 'v', 'We mourn in black.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '我们穿黑色衣服服丧。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('mouse', 'n', 'He remained a mouse in public affairs.', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '在公众事务上他依然胆小如鼠。');
