INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('penny', 'n', 'I only have a few pennies with me.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '我身上只有几个便士。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('penny', 'n', 'We should make every penny count.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '我们每一分钱都要花在刀刃上。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('pepper', 'n', 'The pepper in the food caused me to sneeze.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '这食物中的胡椒味儿呛得我直打喷嚏。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('pepper', 'n', 'This kind of green pepper is very hot.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '这种青辣椒非常辣。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('pepper', 'vt', 'The naughty boys peppered her with snowballs.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '那些淘气的男孩子们向她连掷雪球。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('pepper', 'vt', 'The wall had been peppered with bullets.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '墙上有密集的子弹痕迹。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('pepper', 'vt', 'You don''t have to pepper the soup.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '你不一定要往汤里撒胡椒粉。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('per', 'prep', 'Cars will be supplied as per the terms agreed on.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '汽车将按所有条款达成一致后供应。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('per', 'prep', 'The goods were shipped per steamer.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '货物由船舶运送。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('per', 'prep', 'The vessel tax of anchorage is 5,000 dollars per year.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '这艘船的停泊税一年是五千美元。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('per', 'prep', 'These apples cost five yuan per kilogram.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '这些苹果每公斤五元。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('per', 'prep', 'Tickets to the concert are $50 per head!', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '这场音乐会的门票价格是每人五十美元。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('per', 'prep', 'We were bowling along (the motorway) at seventy miles per hour.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '我们以每小时七十英里的速度(在高速公路上)飞驰。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('performance', 'n', 'He was assiduous in performance of duty.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '他勤勉地履行职责。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('performance', 'n', 'His good performance in school does great credit to his mother.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '他在学校的优良表现为他母亲带来极大的荣誉。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('performance', 'n', 'His performance gripped the audience.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '他的表演深深地吸引住观众。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('performance', 'n', 'It is a credit to Zhang Yimou''s performance in bringing out amazing and unexpected skills in his actors.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '张艺谋总是有本事让演员带来意想不到的、充满新意的表演技巧。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('performance', 'n', 'Performance is less important than reliability in a car.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '汽车的可靠性比其机械性能更重要。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('performance', 'n', 'Proposal is easier than performance.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '建议容易实行难。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('performer', 'n', 'Performer strolls out grumbling and shows his lunchbox empty.', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '执行者漫步抱怨并且显示他的饭盒空。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('performer', 'n', 'She is an accomplished performer.', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '她是一个很有造诣的表演者。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('performer', 'n', 'The performer juggled with knives and swords.', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '表演者耍弄刀剑。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('performer', 'n', 'The performer removes a notepad and pen.', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '执行者去除笔记薄和笔。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('permission', 'n', 'Do I have your permission to change the date?', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '您允许我改变一下日期吗？');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('permission', 'n', 'She refused to give her permission.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '他拒不同意。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('permission', 'n', 'They entered the area without permission.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '他们未经许可擅入该地。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('permission', 'n', 'We carry this essay with his permission.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '我们发表此文得到他的同意。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('permission', 'n', 'You must ask permission if you want to leave early.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '如果你想早走的话，必须得到允许。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('permission', 'n', 'You were wrong to take the car without permission.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '你未得许可就把汽车开走是不应该的。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('persuade', 'vt', 'He is easily persuaded.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '他这人好说话。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('persuade', 'vt', 'How can I persuade you of my sincerity?', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '我如何能够让你相信我的诚意？');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('persuade', 'vt', 'I am not fully persuaded by the evidence.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '这证据不足以使我充分信服。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('persuade', 'vt', 'We are persuaded of the justice of her case.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '我们确信对她案件的审理是公正的。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('persuade', 'vt', 'You try and persuade her (to come out with us).', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '你去试试劝她(跟我们一起出去)吧。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('petrol', 'n', 'My car is rather heavy on petrol.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '我的汽车很费油。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('petrol', 'n', 'My new car really runs away with the petrol.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '我的新汽车耗油太多了。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('petrol', 'n', 'Petrol is volatile.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '汽油容易挥发。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('petrol', 'n', 'The petrol is running out.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '汽油快要耗尽了。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('petrol', 'n', 'The police faced a mob throwing bricks and petrol bombs.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '警察面对一群投掷砖块和汽油弹的暴徒。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('petrol', 'n', 'They had to ration petrol during the war.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '战时他们不得不限量供给汽油。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('petrol', 'n', 'They have increased the price of petrol again.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '他们又提高了汽油的价格。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('photograph', 'n', 'I planned to enlarge this photograph.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '我计划放大这张照片。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('photograph', 'n', 'This photograph flatters you.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '这照片比你本人漂亮。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('photograph', 'vi', 'I always photograph badly.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '我照相总是照不好。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('photograph', 'vi', 'Some people just don''t photograph well.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '有些人就是不上相。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('photograph', 'vt', 'Stand still while I photograph you.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '我给你照像，别动。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('photographer', 'n', 'The photographer retouched her picture.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '摄影师对她的照片作了修整。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('phrase', 'n', 'Four whipping bars, ff as well as marcatissimo, put a more or less vicious and peremptory end to the phrase.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '四个鞭打般的小节，以带强调的最强音给乐句一个或多或少野蛮而断然的结束。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('phrase', 'n', 'He hashed a phrase in order to colour the sentence.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '他反复推敲一个习语以使该句增色。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('phrase', 'n', 'Notice syncopation in the second phrase where the accent falls after the first beat.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '注意第二乐句中的切分音，重音在第一拍之后出现。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('phrase', 'n', 'The new phrase is now on the tongue of men.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '这个新短语现在正十分流行。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('phrase', 'n', 'The speaker certainly knew how to turn a phrase.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '该讲话者当然懂得如何措词。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('phrase', 'n', 'The teacher dictated the phrase to the class.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '老师让全班同学听写这个短语。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('phrase', 'vt', 'He paused as if to find a way to phrase his next thoughts.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '他顿了一下，象是在考虑接下去要说的意思该怎样来措词表达。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('phrase', 'vt', 'She will phrase a fugue of Bach''s in these days.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '这几天她将把巴赫的赋格曲分成旋律分句。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('physician', 'n', 'He consulted his physician about his health.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '他请医生诊断他的健康。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('physician', 'n', 'He prefers to be a surgeon rather than a physician.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '他喜欢当外科医生而不愿作内科医生。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('physician', 'n', 'He was attended by his personal physician.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '他的私人医生护理他。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('physician', 'n', 'The physician put him on a special diet.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '内科医生给他吃特定的食物。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('physicist', 'n', 'He came to the fore as a physicist at an early age.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '他在早年就成了杰出的物理学家。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('physicist', 'n', 'The physicist fudged on his experimental data.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '那位物理学家捏造实验数据。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('pianist', 'n', 'It takes years of discipline to become a pianist.', 1, '2019-10-09 18:33:06', '2019-10-09 18:33:06', '要成为钢琴家需经多年训练。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('pianist', 'n', 'The pianist accompanied her singing.', 1, '2019-10-09 18:33:06', '2019-10-09 18:33:06', '钢琴家为她的歌唱伴奏。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('pill', 'n', 'Father must take his pill on the hour.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '父亲必须在每点钟准点吃药丸。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('pill', 'n', 'His mother told him to have a vitamin pill before leaving.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '他妈妈让他在走之前吃一颗维生素药丸。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('pill', 'n', 'Occasionally she had to take sleeping pills.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '她偶尔不得不吃安眠药。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('pill', 'n', 'The baby spat out the nasty pill.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '婴儿吐出了难吃的药丸。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('pill', 'n', 'This pill will help to settle your nerves.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '你吃了这片药神经就不那麽紧张了。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('pill', 'v', 'She was nervous about returning to the pill because of worries about a possible cancer risk.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '因为担心可能患癌症的风险，她对再服用药丸很紧张。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('pillow', 'n', 'He bought a foam pillow.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '他买了一个海绵枕头。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('pillow', 'n', 'I want to consult with my pillow before I take a decision.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '在我做出决定前，我要好好考虑一下。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('pillow', 'n', 'My daughter asked me to buy her a pillow stuffed with feathers.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '我女儿请求我给她买一个羽绒枕头。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('pillow', 'vt', 'Birdlike, he retired at dusk, lying on the bare floor with a wooden block under his head like a pillow.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '在黄昏时分，他象鸟儿一样就寝，只用一块木头搁在头下面当作枕头，躺在光溜溜的地板上。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('pillow', 'vt', 'Her arm pillowed the sleeping child.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '熟睡的孩子枕着她的手臂。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('pillow', 'vt', 'She lay down on the bed and put her head on the pillow tiredly.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '她在床上躺下来，脑袋疲惫地搁在枕头上。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('pillow', 'vt', 'The earth will pillow my head tonight.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '今夜我将露宿。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('pine', 'n', 'It''s a noted place for its pine tree and springs.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '这是一个以松树和泉水著称的地方。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('pine', 'n', 'Pine is a soft wood and teak is a hard wood.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '松木是软木，柚木是硬木。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('pine', 'n', 'The pine trees defy severe cold.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '松树不畏严寒。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('pine', 'n', 'There is a pine forest near our house.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '我们家附近有片松树林。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('pine', 'vi', 'Alone,a lonely stranger in a foreign land,I pine for kinsfolk doubly on a holiday.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '独在异乡为异客每逢佳节倍思亲');
