INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('puzzle', 'vi', 'She listened with a puzzled expression on her face.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '她脸上带著困惑的表情在倾听。');
