INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('quarrel', 'n', 'After supper the quarrel lapsed into silence.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '晚饭后争吵逐渐平息下来了。');
