INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('quarrel', 'n', 'Their small argument boiled over into a serious quarrel.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '他们的小争论终于酿成了一场激烈的争吵。');
