INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('quarrel', 'vi', 'Few people would quarrel with the validity of the principle.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '几乎没人能挑剔这项原则的正确性。');
