INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('queue', 'n', 'At the head of the queue was an old woman.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '在队列的前面有一个老妇人。');
