INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('race', 'n', 'Every morning he spent two hours training for the race.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '他每天早晨花两个小时练习赛跑。');
