INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('reward', 'vt', 'They rewarded the winners with gifts of fruits and flowers.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '他们奖给优胜者一些水果和鲜花。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('rid', 'vt', 'It is necessary to get rid of bureaucratic practices.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '必须清除官僚习气。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('rid', 'vt', 'We have an extractor fan in the kitchen to get rid of the smell of cooking.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '我们的厨房里有台排气扇，用来消除油烟。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('rid', 'vt', 'We must try every way to rid ourselves of these debts.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '我们要想尽一切办法来摆脱这些债务。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('rid', 'vt', 'We will be glad to get rid of him.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '摆脱他，我们将很高兴。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('rid', 'vt', 'You must rid yourself of this melancholy mood.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '你一定要摆脱这种忧愁情绪。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('ripe', 'adj', 'I picked a ripe apple from the tree.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '我从树上摘下一只熟的苹果。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('ripe', 'adj', 'It was mid-summer when the crops were ripe.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '庄稼熟的时候已是仲夏。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('ripe', 'adj', 'Most tomatoes are red when ripe, but some kinds are yellow.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '大多数西红柿成熟时是红色的，但有些品种是黄色的。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('ripe', 'adj', 'The branches were weighed down with ripe apples.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '苹果成熟了把树枝都压弯了。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('ripe', 'adj', 'The ripe apples picked easily.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '成熟的苹果易于采摘');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('ripe', 'adj', 'The time was ripe for a challenge to the power of the government.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '时机已经成熟，可以向政府权力提出挑战了。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('ripe', 'adj', 'These apples are not ripe; they are sour.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '这些苹果不熟，是酸的。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('rooster', 'n', 'Don''t talk with him; he is a rooster.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '别理他，他太狂妄自负了。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('rooster', 'n', 'The rooster clawed a hole in the earth.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '那只公鸡在地上扒出个洞来。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('rooster', 'n', 'The rooster disappeared under the car.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '公鸡消失在车子底下。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('root', 'n', 'Ann caught her foot on a tree root and stumbled.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '安的一只脚被树根绊了一下跌倒了。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('root', 'n', 'I finally got to the root of the problem.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '我们最终抓住了问题的实质');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('root', 'n', 'Money is thought to be the root cause of all evils.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '金钱被认为是万恶之源。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('root', 'n', 'The dog is snuffling around the root of a tree.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '那条狗在树根处嗅来嗅去。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('root', 'n', 'Unhappiness is the root cause of his illness.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '不开心是他生病的根源。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('root', 'v', 'After a few months I felt I had really taken root here.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '几个月之后，我感到确实在这里扎根了。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('root', 'v', 'It is vain to attempt to root out heresy by force.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '试图用暴力消除异端邪说是徒劳的。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('root', 'v', 'Roses will not root in such acid soil.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '玫瑰在这种酸性土壤中不会生根。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('root', 'v', 'The pegged-down branches of the plant will take root.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '固定的植物的枝条将会扎根。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('rot', 'n', 'Foot rot is an avoidable affiction.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '烂蹄病是可以避免的。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('rot', 'n', 'He''s talking rot when he says that the company is almost bankrupt.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '他说这家公司快要破产了，简直是胡说八道。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('rot', 'n', 'Rot in the tree trunk caused the tree to fall.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '树干的腐烂导致树倒了。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('rot', 'n', 'The roof timbers were affected by rot and insect attack.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '屋顶的木料已经腐朽并遭虫害。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('rot', 'n', 'The rot set inwhen we lost that important customer in Japan.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '我们失去了日本的那家重要客户後,情况越来越糟了。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('rot', 'n', 'They''re bound to win.'' Rot! They haven''t a chance!''', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '｀他们一定赢.');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('rot', 'n', 'We should stop it the minute the rot sets in.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '腐败一产生，我们就应立即制止住。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('rot', 'v', 'The meat will rot if it isn''t kept cool.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '肉如果不冷藏就会腐败。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('rot', 'v', 'The prisoner is left to rot in the jail.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '这个囚徒被投入监狱任凭他渐渐消瘦下去。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('rot', 'v', 'They didn''t buy the house because it had dry rot.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '他们没有买那座房子，因为房子的木料已经干朽了。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('rot', 'v', 'This cheap wine will rot your stomach.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '这种劣质葡萄喝了会伤胃。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('rot', 'v', 'Too much television rots your brain.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '多看电视会使你的头脑退化。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('roundabout', 'adj', 'I entered the village by the little roundabout lane.', 1, '2019-10-09 18:33:03', '2019-10-09 18:33:03', '我由一条迂回曲折的小径进了村。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('roundabout', 'adj', 'She suggested it in a very roundabout way.', 1, '2019-10-09 18:33:03', '2019-10-09 18:33:03', '她很婉转地提出了这个建议。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('roundabout', 'adj', 'The taxi didn''t go by the direct route, but in a roundabout way.', 1, '2019-10-09 18:33:03', '2019-10-09 18:33:03', '出租车没有走直路，而是绕道行驶。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('roundabout', 'adj', 'We drove home by a roundabout route.', 1, '2019-10-09 18:33:03', '2019-10-09 18:33:03', '我们绕远道驾车回家。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('roundabout', 'n', 'The car stalled at the roundabout.', 1, '2019-10-09 18:33:03', '2019-10-09 18:33:03', '那辆汽车在环形交叉处抛锚了。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('roundabout', 'n', 'The kids were pushing the roundabout at a giddy speed.', 1, '2019-10-09 18:33:03', '2019-10-09 18:33:03', '孩子们推动着旋转平台快得令人眩晕。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('roundabout', 'n', 'There was a slight noise behind her and she turned just in time to seize a small boy by the slack of his roundabout and arrest his flight.', 1, '2019-10-09 18:33:03', '2019-10-09 18:33:03', '这时在她身后传来一声轻微的响声，她转身一把抓住了一个小男孩的短外套的衣角，他想跑都跑不掉了。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('roundabout', 'n', 'Turn left at the next roundabout.', 1, '2019-10-09 18:33:03', '2019-10-09 18:33:03', '在下一个环形交叉路口向左转。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('ruin', 'n', 'He is verging on ruin.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '他正濒临毁灭。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('ruin', 'n', 'His rashness led ultimately to his ruin.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '他的急躁鲁莽最终导致了他的毁灭。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('ruin', 'n', 'The minister''s enemies engineered his ruin.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '部长的敌人密谋陷害他。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('ruin', 'n', 'Tom visualized the house as a romantic ruin.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '汤姆把那所房子想像成有传奇色彩的废墟。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('ruin', 'vt', 'He ruined his prospects by carelessness.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '他因疏忽大意而断送了前途。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('ruin', 'vt', 'Her nervousness threatens to ruin the whole idea.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '她的惊慌失措可能会破坏整个计划。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('ruin', 'vt', 'His enemies maliciously conspired to ruin him.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '他的敌人恶毒地密谋搞垮他。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('ruin', 'vt', 'Only great efforts can retrieve the firm from ruin.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '只有巨大的努力才能挽救这个濒于破产的公司。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('ruin', 'vt', 'The politics of the company will ruin their image.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '公司的策略会毁掉他们的形象。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('ruin', 'vt', 'The rain ruined my painting.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '这场雨把我的画给毁了。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('ruin', 'vt', 'These changes spell ruin for the company.', 1, '2019-10-09 18:33:07', '2019-10-09 18:33:07', '这些变化意味着该公司破产。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('running', 'adv', 'He has won this tournament for four years running.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '通过连续四年的努力，他获得了竞标赛冠军。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('running', 'n', 'He deputed the running of the department to an assistant.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '他把部门的管理工作委托给助手了。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('running', 'n', 'His overcoat hampered his running.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '他的大衣妨碍他跑步。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('running', 'n', 'Management is a function running.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '管理是职能的运转。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('running', 'n', 'Running is more boring than basketball.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '跑步比篮球更枯燥.');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('running', 'n', 'The headmaster plays an important role in the running of a school.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '校长在学校工作的运转中起着重要作用。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('running', 'n', 'We should all have a say in the running of our government.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '我们大家在政府的管理这个问题上都应有发言权。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('sadness', 'n', 'His sudden death was occasion for much sadness.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '他的突然死亡是很多的悲哀场合。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('sadness', 'n', 'Try to pin down the exact quality of its sadness.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '努力弄清楚它的确切的悲哀特点。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('sail', 'n', 'Lower the sail or the boat will be knocked down.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '把帆放下来，否则船会被风吹翻的。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('sail', 'n', 'The sailors heaved at rope to tighten the sail.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '水手们用力拉绳把帆绷紧。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('sail', 'n', 'The ship is under sail, making toward the land.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '船正朝大陆扬帆航行。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('sail', 'n', 'They go for a sail down the Thames .', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '他们乘船沿泰晤士河航行。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('sail', 'n', 'Viking River Cruises and Avalon Waterways are some of the operators which sail the Yangtze.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '航海巡线和亚洲龙水路线路由在长江航行的人提供。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('sail', 'v', 'Can you sail a boat?', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '你会驾驶船只吗？');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('sail', 'v', 'The ship was given the all-clear to sail again.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '这艘船得到恢复航行的许可。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('sail', 'v', 'This ship sails for New York on Monday.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '这船将于星期一开往纽约。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('salary', 'n', 'His money comes mainly from salary.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '他的钱主要来自工资。');
