INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('salary', 'v', 'Don''t worry about him, he will be salaried by a big company.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '别为他担心,一家大公司会给他发薪水。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('salary', 'v', 'Like those who work for government or nonprofit organizations, they are salaried rather than paid a fee.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '同在政府或非盈利性组织工作的会计师一样，他们是挣工薪而不是按每笔业务收费。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('sale', 'n', 'Is this shampoo on sale today?', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '这种洗发水今天减价出售吗？');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('sale', 'n', 'The law forbids the sale of alcohol to people under 18.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '法律禁止向18岁以下的人出售含有酒精的饮料。');
