INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('starve', 'vt', 'It is silly to starve yourself to be skinny.', 1, '2019-10-09 18:33:22', '2019-10-09 18:33:22', '显得有骨感而挨饿是愚蠢的.');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('starve', 'vt', 'The enemy is trying to starve us out.', 1, '2019-10-09 18:33:22', '2019-10-09 18:33:22', '敌人想把我们饿出来。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('starve', 'vt', 'They tried to starve him into submission.', 1, '2019-10-09 18:33:22', '2019-10-09 18:33:22', '他们试图用饥饿来使他屈服。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('steak', 'n', 'I want my steak well-done.', 1, '2019-10-09 18:33:03', '2019-10-09 18:33:03', '我要全熟的牛排。');
