INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('stepmother', 'n', 'His stepmother is kind to her.', 1, '2019-10-09 18:33:05', '2019-10-09 18:33:05', '他的继母对她很和善。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('stepmother', 'n', 'If worst comes to worst and I''m driven out by my stepmother, I''ll go begging.', 1, '2019-10-09 18:33:05', '2019-10-09 18:33:05', '万一出现最坏的情况，我被继母赶出家门，我就去乞讨。');
