INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('steward', 'n', 'He is the steward of that great estate.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '他是那笔大财产的管理人。');
