INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('steward', 'n', 'Sometimes he sent his house steward in his place.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '有时他派管家替他去。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('steward', 'n', 'The stewards will inspect the course to see if racing is possible.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '那些干事将检视赛马场看是否适宜比赛。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('steward', 'n', 'We thanked the steward for his good service.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '我们感谢了乘务员所提供的优质服务。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('steward', 'v', 'He was selected to steward a team.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '他被指派经管球队。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('stewardess', 'n', 'An airline passenger is talking to the stewardess in-flight about a briefcase he has left behind.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '一位旅客正和女乘务员谈话，讲他遗失了公事包，忘了带上飞机。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('strait', 'adj', 'My old aunts are very strait laced.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '我的老姨妈都很古板。');
