INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('strength', 'n', 'By doing so, you can test the strength of steel.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '这样做,你可以试验一下钢的强度。');
