INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('strength', 'n', 'She prayed for strength in her troubles.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '她祈求(上帝)在困难时给她力量。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('strength', 'n', 'Tolerance is one of her many strengths.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '宽容是她的一个优点.');
