INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('suffer', 'vi', 'His business suffered greatly during the war.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '他的生意在战争中蒙受了重大损失。');
