INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('suffering', 'n', 'He watched her suffering with clinical detachment.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '他冷漠地看著她遭受的痛苦。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('suffering', 'n', 'Her words mitigated my suffering.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '她的话减轻了我的痛苦。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('suffering', 'n', 'She bore her sufferings bravely.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '她勇敢地承受艰难困苦。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('suffering', 'n', 'To some life means pleasure, to others suffering.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '对一些人来说，人生的意义是享乐，对另外一些人来说则是受苦。');
