INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('suit', 'n', 'The government granted the minority group''s suit for autonomy.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '政府批准了这一少数民族申请自治的请求。');
