INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tick', 'v', 'My watch doesn''t tick because it''s electronic.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '我的表不滴答作响,因为它是电子表。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tick', 'v', 'That''s what makes the world tick.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '使世界持续活动的动力，就在于此。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tick', 'v', 'The clock ticks very loudly.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '这只钟发出很响的滴答声。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tick', 'v', 'The teacher ticks off a name on a list.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '老师在名单上的一个名字旁打勾。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tight', 'adj', 'Don''t they know how tight my schedule is?', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '他们不晓得我的日程安排有多紧吗?');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tight', 'adj', 'Since money was tight, we settled for a short trip to the south.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '既然钱紧了一点,我们退而求其次,来一趟短期的南部之旅。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tight', 'adj', 'That fat woman looked ridiculous in her tight golden dress.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '那位胖妇人穿着金色的紧身礼服，显得滑稽可笑。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tight', 'adj', 'This drawer is too tight for me to open it.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '这抽屉太紧了，我打不开。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tight', 'adj', 'This pair of trousers is too tight for me.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '这条裤子我穿太紧了。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tight', 'adv', 'His fists were clenched tight.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '他紧握双拳。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tight', 'adv', 'If markets suddenly become more volatile, hedge funds can very quickly unwind their short yen positions, whereas households are more likely to sit tight.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '若市场波动水平突然增大，对冲基金能够迅速地对日元空头头寸进行平仓，然而家庭投资者则倾向于采取观望态度。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tight', 'adv', 'She held her baby tight in her arms.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '她紧紧地搂着她的孩子。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tight', 'adv', 'With a bread knife Miss Martha quickly made a deep slash in each of the stale loaves, inserted a generous quantity of butter, and pressed the loaves tight again.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '玛莎小姐迅速地在两条面包上分别深深地切了一刀，慷慨地加进了好多黄油，然后再把面包压实。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('timetable', 'n', 'According to the school timetable, Xiaoming and his classmates will have more courses next semester.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '从课程表看，小明他们下个学期的课程比这个学期还要多。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('timetable', 'n', 'I''ll change my timetable to fay in with yours.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '我将改变时间表以便和你们的吻合起来。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('timetable', 'n', 'Learning about money is already part of the timetable for some of the pupils at this school.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '对于这所学校的小学生来说，有关金钱学习的内容已被列入课程表。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('timetable', 'n', 'The new timetable will come into effect next month.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '新的火车时刻表下个月生效。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('timetable', 'n', 'The timetable is subject to change without notice.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '本时刻表随时可能改变，不另行通知。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('timetable', 'n', 'There have been a few alterations to the timetable.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '时间表上有一些变动。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tip', 'n', 'He charged me 20 yuan for tip.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '他向我要了20元小费.');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tip', 'n', 'It''s common to leave a tip in many Western countries.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '在很多西方国家,给小费是很平常的。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tip', 'n', 'This manual is full of useful tips.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '这本小册子里有很多实用的小建议。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tip', 'v', 'Did you remember to tip the porter?', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '你记得给搬运工小费了吗？');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tip', 'v', 'Only the tip of an iceberg pokes up above water.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '只有冰山的尖端突出于水面。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tire', 'n', 'The tires of this car don''t have enough air in them.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '这辆汽车的轮胎气不足了。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tire', 'n', 'There is no tread on the tire - you should put on a new one.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '这条轮胎已经磨平了，你应该换一条新的。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tire', 'v', 'I never tire of reading Shakespeare.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '我对莎士比亚的作品百读不厌。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tire', 'v', 'I think I must tire her dreadfully.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '我想我一定很惹她讨厌。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tire', 'v', 'I''m afraid that trip will tire me out too much.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '我怕出去那么一趟会把我累坏了。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tire', 'v', 'The ceremony was short so as not to tire them.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '仪式很简短，以免使他们疲劳。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tire', 'v', 'The purpose was to tire out the enemy.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '目的是疲惫敌军。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tiresome', 'adj', 'Deliver me from such tiresome people.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '让我离开这些讨厌的人们吧。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tiresome', 'adj', 'He can be a very tiresome child at times.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '他有时候会是一个非常讨厌的孩子。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tiresome', 'adj', 'His speech is really tiresome.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '他的演讲实在令人厌倦。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tiresome', 'adj', 'I had planned to go to your party, but you know I had to finish that tiresome paper.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '我原本打算去参加你的晚会，可你知道我还得完成那令人厌倦的考卷。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tobacco', 'n', 'A high amount of money is spent on tobacco every year.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '每年都要花费大量金钱在烟草上。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tobacco', 'n', 'He rammed the tobacco into his pipe.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '他把烟丝塞进烟斗。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tobacco', 'n', 'His breath reeked of tobacco.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '他口中带有烟草的臭味。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tobacco', 'n', 'It''s forbidden to sell tobacco to children under 16.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '禁止向16岁以下的儿童出售烟草。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tobacco', 'n', 'Tobacco and alcoholic drinks are taxed heavily in their country.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '烟酒在他们的国家都被课以重税。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('Tokyo', 'n', 'She dropped me off downtown in Tokyo.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '她在东京同我分手了。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('Tokyo', 'n', 'Tokyo is a human beehive of a city.', 1, '2019-10-09 18:33:08', '2019-10-09 18:33:08', '东京是个像蜂窝般人囗集中而热闹的都市。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tomb', 'n', 'A group of student placed a wreath at the tomb of the national hero.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '一群学生在那位民族英雄墓前献上花圈。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tomb', 'n', 'A tombstone stands in front of her tomb.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '她墓前立着一块墓碑。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tomb', 'n', 'The archaeologist researched into the tombs of many of England''s literary great.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '这位考古学家对许多英国文坛宗师的坟墓进行了研究。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tomb', 'n', 'Those dead soldiers were buried in the tombs.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '那些死去的战士被埋葬在坟墓里。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tomb', 'vt', 'In the opening verse we find the women coming to the tomb to anoint the body for burial.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '在本章一开始的经节中，我们看到一些妇女来到坟墓前，为埋葬耶稣用油膏耶稣的身体。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tomb', 'vt', 'When it died, it was interred in a specially prepared tomb in the Valley of the Kings.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '它死了的时候，它被埋葬在国王谷中特定的一座坟墓里。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tongue', 'n', 'English is her mother tongue.', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '英语是她的母语。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tongue', 'n', 'He speaks English, but his native tongue is German.', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '他会说英语，但他的母语是德语。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tongue', 'n', 'That woman has a very sharp tongue.', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '那个女人说话非常尖刻。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tongue', 'n', 'The dog''s tongue was hanging out.', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '这条狗把舌头伸在外面。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tongue', 'n', 'The tongue is one of the vocals.', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '舌头是发声器官之一。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tongue', 'n', 'There appear long tongues of shadows in the sky..', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '天空出现长长的舌状影子。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tongue', 'v', 'The dog tongued the meat on the ground.', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '狗用舌头舔了舔地上的肉。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tongue', 'v', 'The tongue is one of the organs for pronunciation.', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '舌是发音器官之一。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tongue', 'v', 'Try to find out the correct mouth and tongue positions for making these sounds and practice saying words containing them.', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '尽量尝试去发现这些发音的正确口型及舌头位置，并通过反复训练来巩固正确的发音。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('toothpaste', 'n', 'I must buy a tube of toothpaste.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '我必须买一支牙膏。');
