INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('topic', 'n', 'Can we shunt the talks onto a new topic?', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '我们谈话换个新话题好吧？');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('topic', 'n', 'Lack of space forbids further treatment of the topic here.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '因篇幅所限，此处不便对本题作进一步阐述。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('topic', 'n', 'Let''s take leave of the topic and turn to another.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '这个问题就说到这里吧，现在来谈谈另一个话题。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('topic', 'n', 'The essay isn''t even remotely relevant to the topic.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '这篇文章毫不切题。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('topic', 'n', 'The topic seemed to have a fascination for her.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '这个题目似乎对她很有吸引力。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('topic', 'n', 'They discussed the weather and other topics.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '他们讨论了气候和其他的话题。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('topic', 'n', 'This essay is so short that it can only scratch the surface of the topic.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '这篇文章很短，只能对这一问题作肤浅的探讨。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tortoise', 'n', 'Do you remember the story about race between the hare and the tortoise?', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '你们还记得那个关于龟兔赛跑的故事么？');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tortoise', 'n', 'He bought two small tortoises for me.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '他给我买了两只小乌龟。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tortoise', 'n', 'The tortoise crept along at an agonizingly slow speed.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '乌龟以极其缓慢的速度爬行着。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('totally', 'adv', 'He immersed himself totally in his work.', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '他埋头于工作。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('totally', 'adv', 'I am totally bewildered by the clues to this crossword puzzle.', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '这个纵横字谜的提示完全把我弄糊涂了。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('totally', 'adv', 'The details of the costume were totally authentic.', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '这件古装的每个小地方都完全真实。');
