INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tourism', 'n', 'The island has been ruined by tourism.', 1, '2019-10-09 18:33:03', '2019-10-09 18:33:03', '该岛毁在旅游业上了。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tourism', 'n', 'Tourism is a major source of income for the people of Thailand.', 1, '2019-10-09 18:33:03', '2019-10-09 18:33:03', '观光是泰国人很重要的收入来源。');
