INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('tourism', 'n', 'We should work on improving our tourism industry.', 1, '2019-10-09 18:33:03', '2019-10-09 18:33:03', '我们应该努力改进我们的旅游业。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('towel', 'n', 'I dry my hands and face with the towel.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '我用毛巾擦干了双手和面部。');
