INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('uncertain', 'adj', 'I told him what I thought of him in no uncertain terms!', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '我毫不含糊地把我对他的看法告诉了他。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('uncertain', 'adj', 'I''m uncertain whether to go or not.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '我不能肯定去还是不去。');
