INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('uncertain', 'adj', 'The uncertain weather delayed our departure.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '天气变换莫测，延误了我们启程。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('uncertain', 'adj', 'This is a very uncertain time for the global economy.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '当前全球经济正面临一个极不稳定的时期。');
