INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('undivided', 'adj', 'She is looking at the maps with undivided attention.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '她正在全神贯注地看地图。');
