INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('unfair', 'adj', 'It''s unfair to devalue anyone''s work unjustly.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '不公正地贬低任何人的工作都是不公平的。');
