INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('will', 'aux', 'I will do my best.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '我将会尽力而为。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('will', 'aux', 'I will make sacrifices solely on your account.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '单单为了你,我也愿意作出牺牲。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('will', 'aux', 'You will report to me afterward.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '你稍后要向我报告。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('will', 'n', 'A country must have the will to repel any invader.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '一个国家得有决心击退任何入侵者。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('will', 'n', 'Free will makes us able to choose our way of life.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '自由的意志使我们可以选择自己的生活方式。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('will', 'n', 'She was married against her will.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '她结婚是有违本意的。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('will', 'n', 'The rich man left his son nothing in the will.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '那位富翁在遗嘱中什么都没给他儿子留下。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('will', 'vi', 'Call it what you will, it''s still a problem.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '不管怎么说,这仍旧是个问题。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('will', 'vi', 'You''re free to travel where you will in the country.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '在这个国家里，你想上哪儿去就可以上哪儿去。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('will', 'vt', 'He was convinced he could win if he willed it enough.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '如果他坚定决心,他确信自己可以赢。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('will', 'vt', 'He willed most of his money to charities.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '他把钱大多遗赠给慈善机构了。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('will', 'vt', 'This happened because God willed it.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '发生了这件事是上帝的旨意。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('willing', 'adj', 'Are you willing to help?', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '你愿意帮忙吗？');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('willing', 'adj', 'He is always a willing helper.', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '他是一个乐于帮忙的人。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('willing', 'adj', 'I don''t know whether he is willing to come.', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '我不知道他是否愿意来。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('willing', 'adj', 'I wouldn''t be willing.', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '我不会愿意的。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('willing', 'adj', 'I''d like to buy your house if you are willing to sell it.', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '如果你愿意卖的话，我想买你的房子。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('willing', 'adj', 'She''s the willing horse in the office and so gets given most of the work to do.', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '她是办公室里埋头苦干的人，大部分的工作都交给她去做。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('willing', 'adj', 'The spirit is willing but the flesh is weak.', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '心有余而力不足。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('willingly', 'adv', 'He listened to reason and willingly accepted his comrades''opinions and criticisms.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '他听从劝告，乐意地接受了同志们的意见和批评。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('willingly', 'adv', 'If you won''t do it willingly,I''ll make you do it.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '如果你不心甘情愿地干，我就强迫你干。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('willingly', 'adv', 'She will follow me willingly as I roam from one great river to the next.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '她会心甘情愿地跟着我从一条大河跑向另一条大河。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('windbreaker', 'n', 'I want to buy a windbreaker so that I will look more handsome.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '我想买一件风衣，以便使我看起来更帅。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('wipe', 'n', 'Life is like a mop, using today to mop up yesterday and wipe out tomorrow.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '生命就像抹布用今天擦去昨天擦亮明天。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('wipe', 'n', 'Please give the table mats a quick wipe.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '请把桌上的盘碗垫擦一下。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('wipe', 'n', 'Please wipe the table with a dry cloth.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '请用乾抹布擦桌子。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('wipe', 'v', 'He tried to wipe out the memory of his former girlfriend.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '他试图抹去对前任女友的回忆。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('wipe', 'v', 'He wiped me with his fist.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '他用拳头打我。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('wipe', 'v', 'How could I trace your hair to wipe your tears?My hands reached out, catching nothing but a raindrop, on a leaf that had withered.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '多想摩娑你的发，温柔地拭去你的眼泪，我伸出手，却只碰到枯叶上的一滴落雨！');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('wipe', 'v', 'I wiped my shoes on the mat before I came in.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '我在进门前把鞋在垫子上擦了擦。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('wipe', 'v', 'Only in this way can we wipe out the enemy troops.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '只有用这样的方法我们才能消灭敌军。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('wipe', 'v', 'She wiped her tears away.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '她擦掉了眼泪。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('wipe', 'v', 'When you have washed the car , wipe it down well .', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '你把汽车冲洗过后，要好好用布揩拭一下。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('wire', 'n', 'I sent a wire to my mother this morning.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '今天上午我给母亲打了电话。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('wire', 'n', 'The fabric was displayed on a wired stand.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '那种织物陈列在金属制的架子上。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('wire', 'n', 'The hot wire is caused by overload.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '电线发热是由于过载造成的。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('wire', 'n', 'The rope was not strong enough, so we used wire.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '这根绳子不够结实，所以我们用金属线。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('wire', 'vt', 'As soon as the equipment is wired up, you can use it.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '这个设备一接通电源你就能用了。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('wire', 'vt', 'I''d like to wire some money to my son in Seattle.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '我想给我在西雅图的儿子电汇一些钱。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('wire', 'vt', 'The kids are taught how to wire a plug.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '孩子被教导如何在把电线装进塞子。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('wire', 'vt', 'The two pieces of wood were wired together.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '这两块木头已用金属丝捆在一起了。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('wire', 'vt', 'Yon can wire the details to my brother in New York.', 1, '2019-10-09 18:33:13', '2019-10-09 18:33:13', '你可以将细节电告我在纽约的兄弟。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('wisdom', 'n', 'A flow of words is no proof of wisdom.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '口若悬河，不能证明真有才智。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('wisdom', 'n', 'Confucius''s many words are wisdom.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '孔子的很多话都是至理名言。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('wisdom', 'n', 'He attributes his wisdom to his teachers.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '他认为他的智慧来自于老师。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('wisdom', 'n', 'She had acquired much wisdom during her long life.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '她经年累月造就了很高的智慧。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('wisdom', 'n', 'She is crowned with wisdom by nature.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '她天生聪明。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('wisdom', 'n', 'We grow in wisdom as we grow older.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '我们随着年龄增长而更加明智。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('within', 'adv', 'He decorated his house within and without.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '他装修房屋的里面和外面。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('within', 'n', 'Seen from within, the cave looks larger.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '从内部看这山洞显得比较大些。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('within', 'prep', 'I''ll be coming within an hour.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '一小时之内我就会到。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('within', 'prep', 'No fishing is allowed within a twenty mile limit.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '二十英里范围内不准垂钓。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('within', 'prep', 'Several cars are available within this price range.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '在这个价格范围内，有好几种汽车可供选购。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('within', 'prep', 'The noise seems to be coming from within the building.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '吵闹声像是从楼房里传出来的。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('within', 'prep', 'This is a task well within your ability.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '这完全是你力所能及的工作。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('within', 'prep', 'Within these old walls there was once a town.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '在这些旧城墙里面曾经有一座城市。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('wolf', 'n', 'He saw a hungry wolf on the prowl with his telescope.', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '他通过望远镜看到了一头四处觅食的饿狼。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('wolf', 'n', 'Peter is a good footballer, but he is a lone wolf.', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '彼得是个好足球队员，但他是个不与人合作的人。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('wolf', 'n', 'The pups played with the mother wolf.', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '小狼们跟母狼玩耍。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('wolf', 'v', 'I have to wolf down my breakfast, I''m rushed for time.', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '我得快点吃完早餐,时间不够了。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('wolf', 'v', 'With tears in his eyes, he would watch them wolf it down and mutter to himself, "Poor motherless children!', 1, '2019-10-09 18:33:11', '2019-10-09 18:33:11', '看他俩狼吞虎咽的吃那些东西,他眼中含着泪,自言自语的说：“没娘的孩子!”');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('wooden', 'adj', 'He has two big round new Chinese wooden tables.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '他有两张新的中国式的木制的大圆桌。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('wooden', 'adj', 'There is a wooden porch over the sidewalk.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '人行道上有一个木制的门廊。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('wool', 'n', 'Autumn is a lively season for the wool market.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '秋天是毛织品畅销的季节。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('wool', 'n', 'He found a job of shearing wool from sheep.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '他找到一份剪羊毛的工作。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('wool', 'n', 'She asked me to wind the wool for her.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '她叫我帮她绕毛线。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('wool', 'n', 'She passed me a skein of wool.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '她递给我一绞毛线。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('wool', 'n', 'These goats are specially bred for their wool.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '这些山羊是专为剪取羊毛而饲养的。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('wool', 'n', 'They used only natural pigments to dye the wool.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '他们只用天然颜料染毛织品。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('woollen', 'adj', 'A man was lying in the box on top of a pile of woollen goods.', 1, '2019-10-09 18:33:05', '2019-10-09 18:33:05', '一个男人躺在箱子里的一堆毛织品上。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('woollen', 'n', 'China''s woollen textile industry is forging ahead to meet the needs of the country.', 1, '2019-10-09 18:33:05', '2019-10-09 18:33:05', '中国的毛纺工业正迅速发展以适应国家的需要。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('woollen', 'n', 'The woollen cloth prickles (my skin).', 1, '2019-10-09 18:33:05', '2019-10-09 18:33:05', '穿毛料衣服(使我的皮肤)有被扎的感觉。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('workday', 'n', 'An ordinary workday is eight hours.', 1, '2019-10-09 18:33:14', '2019-10-09 18:33:14', '一个工作日通常是八个小时。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('workday', 'n', 'Still, there have been times when her workday was upended by a sick child.', 1, '2019-10-09 18:33:14', '2019-10-09 18:33:14', '尽管如此，有时她的工作日还是会因为孩子生病而被弄得一团乱。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('workday', 'n', 'When a regular workday is ten hours the overtime work may not exceed two hours for that particular day.', 1, '2019-10-09 18:33:14', '2019-10-09 18:33:14', '二当日正常工时达十小时者，其延长之工作时间不得超过二小时。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('workforce', 'n', 'Demographic change is another reason why the workforce is greying.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '人口变化是劳动力老龄化的又一原因。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('workforce', 'n', 'Only about half of the original workforce remains.', 1, '2019-10-09 18:33:09', '2019-10-09 18:33:09', '只剩下大约原来一半的劳动力。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('workplace', 'n', 'The balanced workplace isn''t exactly new.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '均势工作场所确切地说并不是新名词。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('workplace', 'n', 'Workplace harassment can take many forms.', 1, '2019-10-09 18:33:10', '2019-10-09 18:33:10', '构成工作场所骚扰的形式有很多种。');
INSERT INTO word_sentence_h (code, type, sentence, status, create_time, update_time, translation) VALUES ('world-famous', 'adj', 'Charles Chaplin is a world famous comedy actor.', 1, '2019-10-09 18:33:12', '2019-10-09 18:33:12', '查理卓别林是世界闻名的喜剧演员。');
